from database_cfg import Base
from sqlalchemy import Text, Boolean, Integer, Column, ForeignKey


class User_tbl(Base):
    __tablename__='user_tbl'
    user_id=Column(Integer, primary_key=True)
    email=Column(Text)
    usr_password=Column(Text)

   # def __repr__(self):
    #    return (self.user_id, self.email, self.usr_password)


class Article_tbl(Base):
    __tablename__='article_tbl'
    article_id=Column(Integer, primary_key=True)
    user_id=Column(Integer, ForeignKey(User_tbl.user_id))
    article_name=Column(Text)
    article=Column(Text)

class Video_tbl(Base):
    __tablename__ = 'video_tbl'
    video_id = Column(Integer, primary_key=True)
    user_id = Column(Integer, ForeignKey(User_tbl.user_id))
    video_name = Column(Text)
    video_url = Column(Text)

class Comments(Base):
    __tablename__ = 'comments'
    comment_id = Column(Integer, primary_key=True)
    user_id = Column(Integer, ForeignKey(User_tbl.user_id))
    article_id = Column(Integer, ForeignKey(Article_tbl.article_id), default=None)
    video_id = Column(Integer, ForeignKey(Video_tbl.video_id), default=None)
    comments_txt = Column(Text)




