from pydantic import BaseModel

class User(BaseModel):
    email: str
    password: str
    class Config:
        orm_mode = True

class Article(BaseModel):
    user_id: int
    article_name: str
    article: str
    class Config:
        orm_mode = True

class Video(BaseModel):
    user_id: int
    video_name: str
    video_url: str
    class Config:
        orm_mode = True

class Comments(BaseModel):
    user_id: int
    video_id: int = None
    article_id: int = None
    comments_txt: str
    class Config:
        orm_mode = True



